package com.coreJava;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=-128; //Ranges from -128 to 127
		short b=10000;//Ranges from -32,768 to 32,767
		int c=-20000;//Ranges from  - 2,147,483,648 to 2,147,483,647
		long d= 200000L;//Ranges from -9,223,372,036,854,775,808 to 9,223,372,036,854,775,807 
		float e=123.4f;// Used to write numbers in decimal form
		double f=123.4;// default data type for decimal values
		boolean g=true;//Only two possible values: true or false and default is false
		char h='A';//Used to store any character and the character has to be written in single quotes

	}

}
